import socket
import datetime

# cria um socket usando o protocolo IPV4 e conex√£o TCP
s = socket.socket(socket.AF_INET, socket.SOCK_STREAM)

host = "" # 127.0.0.1
port = 7000

msg3 = ""
msg2 = "msg from cli to server"
msg = ""

# conecta com o servidor
s.connect((host, port))

try:
    print("--------------------------")
    print(datetime.datetime.now())
    msg_lenght = str(len(msg))
    print("Sendind message size...")
    s.sendall(msg_lenght.encode("ascii"))
    print("Sendind real message...")
    s.sendall(msg3.encode("ascii"))
    # aguardando a resposta
    data = s.recv(1024)
    # mostrar dados
    print("Data received:", data.decode("ascii"))
    print("Data size:", len(data), "bytes")
finally:
    print("Closing socket...")
    s.close()
